{
    "identifier": "external_account_id_{{$guid}}",
    "metadata": {},
    "mode": "oauth2",
    "oauth2": {
        "redirect_uri": "{{$randomUrl}}",
        "response_type": "code",
        "scope": [
            "r:awx_action:balances_view",
            "r:awx_action:settings.account_details_view"
        ],
        "state": "state-{{$guid}}"
    },
    "prefilled_formdata": [
        {
            "editable": false,
            "field": "email",
            "value": "test.{{$timestamp}}@example.com",
            "verified": true
        },
        {
            "editable": false,
            "field": "business_name",
            "value": "{{$randomCompanyName}}",
            "verified": true
        },
        {
            "editable": false,
            "field": "first_name",
            "value": "{{$randomFirstName}}",
            "verified": true
        },
        {
            "editable": false,
            "field": "last_name",
            "value": "{{$randomLastName}}",
            "verified": true
        },
        {
            "editable": false,
            "field": "contact_name",
            "value": "{{$randomFullName}}",
            "verified": true
        }
    ]
}

{
    "account_details": {
        "beneficial_owners": [
            {
                "identification_type": "PERSONAL_ID",
                "nationality": "CN",
                "attachments": {
                    "identity_files": [
                        {
                            "description": "{{filename}}",
                            "file_id": "{{file_id}}",
                            "tag": "PERSONAL_ID_FRONT"
                        },
                        {
                            "description": "{{filename}}",
                            "file_id": "{{file_id}}",
                            "tag": "PERSONAL_ID_BACK"
                        }
                    ]
                }
            }
        ],
        "business_details": {
            "address": {
                "country_code": "CN"
            },
            "industry_category": "E-Commerce - Merchant",
            "purpose": "Payment to service providers - Shipping & Delivery Companies/Agencies",
            "url": "http://www.example.com",
            "attachments": {
                "business_documents": [
                    {
                        "description": "{{filename}}",
                        "file_id": "{{file_id}}",
                        "tag": "BUSINESS_LICENSE"
                    }
                ]
            }
        },
        "legal_rep_details": {
            "attachments": {
                "identity_files": [
                    {
                        "description": "{{filename}}",
                        "file_id": "{{file_id}}",
                        "tag": "PERSONAL_ID_FRONT"
                    },
                    {
                        "description": "{{filename}}",
                        "file_id": "{{file_id}}",
                        "tag": "PERSONAL_ID_BACK"
                    }
                ]
            },
            "identification_type": "PERSONAL_ID",
            "nationality": "CN"
        }
    },
    "account_usage": {
        "card_usage": [
            "NONE"
        ],
        "collection_country_codes": [
            "CN"
        ],
        "collection_from": [
            "CLIENTS_AND_OTHER_THIRD_PARTIES"
        ],
        "expected_monthly_transaction_volume": {
            "amount": "100000.0"
        },
        "payout_country_codes": [
            "CN"
        ],
        "payout_to": [
            "CONNECTED_AWX_ACCOUNT",
            "VENDORS_SUPPLIERS_OR_OTHER_THIRD_PARTY_BANK_ACCOUNT",
            "WITHDRAWAL_TO_BANK_ACCOUNT_UNDER_THE_SAME_NAME"
        ]
    },
    "customer_agreements": {
        "agreed_to_data_usage": true,
        "agreed_to_terms_and_conditions": true,
        "opt_in_for_marketing": false
    }
}

{
    "type":"US_BANK",
    "us_bank": {
        "account_name": "Connected Account Demo",
        "account_number": "122356789",//replace this with a sample bank account number
        "ach": "021000021",//sample routing number
        "currency": "USD",
        "entity_type": "BUSINESS",
        "account_type": "CHECKING"
    },
    "preferred_verification_type":"MICRO_DEPOSIT",
    "request_id": "{{$guid}}"
}
